        // - - -ENVIAR EMAIL AUTOMATICO---------------------------------------------------------------------------------
        public void EnviaEmail(int idUsuario)
        {
            Usuario UsuarioBuscado = ctx.Usuarios.Find(idUsuario);
            string ToEmail = UsuarioBuscado.Email;

            MailMessage mail = new MailMessage();
            mail.From = new MailAddress(EmailOrigem);
            mail.To.Add(ToEmail);
            mail.Subject = "Pedido Enviado";
            mail.IsBodyHtml = true;
            mail.Body = ($"<htm><body> <img src={ImgHeaderPedidoEnviadoCliente} width='100%' /> <h1 style='color:red'>Pedido Enviado com Sucesso!</h1> <p>Informamos que o seu pedido foi enviado ao Pet Shop e est em anlise.<br></br>Aguarde um pouquinho, que em breve teremos uma resposta.</p><img src={imgRodapeDoEmail} width='100%'></body></html>");

            SmtpClient smtp = new SmtpClient("smtp.outlook.com");
            smtp.Port = 587;
            smtp.EnableSsl = true;
            smtp.DeliveryMethod = System.Net.Mail.SmtpDeliveryMethod.Network;
            smtp.UseDefaultCredentials = false;
            smtp.Credentials = new System.Net.NetworkCredential(EmailOrigem, senhaEmailOrigem);
            smtp.Send(mail);
        }

