        public void EnviaEmailAposAtualizarPedido(string status, string idPedido)
        {
            InfoPedido informacoes = new InfoPedido();
            informacoes = ctx.InfoPedidos.Where(x => x.codigoDePedido == idPedido).FirstOrDefault();
            int idUsuario = informacoes.idUsuario;

            Usuario UsuarioBuscado = ctx.Usuarios.Find(idUsuario);
            string ToEmail = UsuarioBuscado.Email;
            string Titulo = ("Pedido " + status + "!");
            Console.WriteLine(Titulo);


            MailMessage mail = new MailMessage();
            mail.From = new MailAddress(EmailOrigem);
            mail.To.Add(ToEmail);
            mail.Subject = Titulo;
            mail.IsBodyHtml = true;

            if(status == "Cancelado"){
                mail.Body = ($"<htm><body> <img src={ImgHeaderPedidoCancelado} width='100%' /> <h1 style='color:red'>Sentimos muito</h1> <p>Por algum motivo o seu pedido foi rejeitado pelo Pet Shop.<br></br>Tente novamente ou escolha um outro pet shop para realizar o pedido</p><p>Caso o problema persista, por favor entre em contato conosco atravs dos nossos canais de atendimento.</p> <img src={imgRodapeDoEmail} width='100%'></body></html>");
            } else {
                mail.Body = ($"<htm><body> <img src={ImgHeaderPedidoAceito} width='100%' /> <h1 style='color:red'>Pedido Aceito!</h1> <p>Oba, o seu pedido foi aceito!<br></br>Ele estar sendo preparado e em breve ser enviado at voc.<br></br>Obrigado por comprar conosco.</p><img src={imgRodapeDoEmail} width='100%'></body></html>");
            }            
            SmtpClient smtp = new SmtpClient("smtp.outlook.com");
            smtp.Port = 587;
            smtp.EnableSsl = true;
            smtp.DeliveryMethod = System.Net.Mail.SmtpDeliveryMethod.Network;
            smtp.UseDefaultCredentials = false;
            smtp.Credentials = new System.Net.NetworkCredential(EmailOrigem, senhaEmailOrigem);
            smtp.Send(mail);
        }

        public void EnviaEmailAposRetirarPedido( string idPedido, string status)
        {
            InfoPedido informacoes = new InfoPedido();
            informacoes = ctx.InfoPedidos.Where(x => x.codigoDePedido == idPedido).FirstOrDefault();
            int idUsuario = informacoes.idUsuario;

            Usuario UsuarioBuscado = ctx.Usuarios.Find(idUsuario);
            string ToEmail = UsuarioBuscado.Email;

            MailMessage mail = new MailMessage();
            mail.From = new MailAddress(EmailOrigem);
            mail.To.Add(ToEmail);
            mail.IsBodyHtml = true;

            if (status != "Entregue")
            {
                mail.Subject = "Pedido a caminho!";
                mail.Body = ($"<htm><body> <img src={ImgHeaderPedidoACaminho} width='100%' /> <h1 style='color:red'>Pedido a caminho!</h1> <p>O seu pedido j foi retirado pelo entregador e est a caminho.<br></br>Fique atento que logo estar chegando at voc.</p><img src={imgRodapeDoEmail} width='100%'></body></html>");
            }
            else
            {
                mail.Subject = "O seu pedido foi entregue!";
                mail.Body = ($"<htm><body> <img src={ImgHeaderPedidoEntregue} width='100%' /> <h1 style='color:red'>O seu pedido foi entregue!</h1> <p>Esperamos que tenha ocorrido tudo bem com a sua entrega, caso tenha alguma dvida ou sugesto, entre em contato atravs dos nossos canais de atendimento.</p> <p>Gostaramos tambm de pedir uns minutinhos para que possa nos avaliar na sua loja de App:<br></br><strong>Apple Store:</strong>https://apps.apple.com/br/app/pet-food/id1532281148<br></br><strong>Google Play:</strong>https://play.google.com/store/apps/details?id=br.com.app.gpu2417996.gpu29d0dd43fa0eef8cd4a7f2dcf3707158</p><p>Ahh, avalie tambm o pet shop em que fez a compra atravs do nosso App.<br></br>Obrigado por comprar conosco.</p><img src={imgRodapeDoEmail} width='100%'></body></html>");
            }
            SmtpClient smtp = new SmtpClient("smtp.outlook.com");
            smtp.Port = 587;
            smtp.EnableSsl = true;
            smtp.DeliveryMethod = System.Net.Mail.SmtpDeliveryMethod.Network;
            smtp.UseDefaultCredentials = false;
            smtp.Credentials = new System.Net.NetworkCredential(EmailOrigem, senhaEmailOrigem);
            smtp.Send(mail);
        }

